import React, { useState } from "react";
import { useAccordionButton } from "react-bootstrap";
import { IoIosSend } from "react-icons/io";

const Comments = () => {
  const [comments, setComments] = useState([
    {
      username: "",
      comment: "",
    },
  ]);

  const sendComment = (e) => {
    const comment = document.querySelector(".comments-input");

    console.log(comment.value);
    setComments([...comments, comment.value]);
    comment.value = "";
  };

  return (
    <>
      <div className="comments">
        <input
          type="text"
          placeholder="Enter your Comment..."
          className="comments-input"
          // onChange={(e) => sendComment(e)}
        />
        <div className="comments-send" onClick={sendComment}>
          <IoIosSend size={20} color="#01d293" />
        </div>
      </div>
      {comments && (
        <div className="comments-on_post">
          {comments.map((comment) => (
            <div className="comments-each_comment">{comment}</div>
          ))}
        </div>
      )}
    </>
  );
};

export default Comments;
